mapper.insert:
Insert into Review values(
						  #{o_order_num},
						  (
						  	SELECT od.nb_num 
							FROM  
							      Order_detail od,
							      Orderr o
							WHERE o.o_order_num  = od.o_order_num
							AND   o.o_order_num  = #{o_order_num}
							AND   od.nb_num      = #{nb_num}
						   ),
						  #{r_title},
						  #{r_content},
						  CURRENT_TIMESTAMP,
						  #{r_rating}
						  )

mapper.select:
SELECT count(*) 
FROM   Review
WHERE  nb_num = ${nb_num}

SELECT count(*) 
FROM   Review 
Where  r_rating = #{r_rating}
AND    nb_num = ${nb_num}

SELECT ROUND(AVG(r_rating),0) 
FROM   review
WHERE  nb_num = ${nb_num}

SELECT * FROM ( SELECT rownum rn , a.* FROM (SELECT r.*, m.m_name, m.m_image FROM Review r ,orderr o ,member m ,newbook nb Where r.o_order_num = o.o_order_num AND o.m_num = m.m_num AND r.nb_num = nb.nb_num AND nb.nb_num = #{nb_num}
order by r_create_date DESC
order by r_rating DESC, r_create_date DESC
order by r_create_date DESC
) a ) WHERE rn BETWEEN 1 and #{end}

SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT r.*, o.o_order_date, n.nb_title, n.nb_image, od.o_de_count FROM review r , orderr o , newbook n , order_detail od
WHERE r.o_order_num = o.o_order_num and o.m_num = #{m_num} AND r.nb_num = n.nb_num AND r.o_order_num = od.o_order_num AND r.nb_num = od.nb_num order by r_create_date DESC ) a
) WHERE rn BETWEEN #{start} and #{end}

SELECT r.*, nb.nb_image, nb.nb_title
FROM   review r, newbook nb
WHERE  r.nb_num = nb.nb_num
AND    r.o_order_num = #{o_order_num}
AND    r.nb_num      = #{nb_num}

mapper.update:
 	UPDATE review
	SET
	r_title        = #{r_title},
	r_content      = #{r_content},
	r_create_date  = CURRENT_TIMESTAMP,
	r_rating       = #{r_rating}
	
	WHERE o_order_num = #{o_order_num}
	AND   nb_num      = #{nb_num}

mapper.delete:
 	DELETE 
	FROM   review
	WHERE  o_order_num = #{o_order_num}
	AND    nb_num      = #{nb_num}
